CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_estado_presentismo_por_fecha`(
    IN p_fecha DATE
)
BEGIN
    SELECT 
        n.Legajo,
        n.usuario,
        n.servicio,
        COALESCE(
            CASE 
                WHEN c.usuario IS NOT NULL THEN 'Conectado'
                WHEN j.codigo_motivo IS NOT NULL THEN mc.descripcion
                ELSE 'AI'
            END,
            'AI'
        ) AS estado,
        c.conexion,
        c.desconexion,
        j.Fecha AS fecha_justificada,
        j.codigo_motivo,
        j.usuario_carga
